mapper.update:
UPDATE users
SET username = #{username},
    email = #{email},
    phone = #{phone},
    address = #{address}
WHERE user_idx = #{user_idx}

UPDATE business
SET business_name = #{business_name},
    business_number = #{business_number},
    started_date = #{started_date},
    contentId = #{contentId}
WHERE business_idx = #{business_idx}
